{
  "aql":  "select a/uid/value as uid, a_a/data[at0001]/items[at0002]/value/value as Problem_Diagnosis_name from EHR e[ehr_id/value='{{ehrId}}'] contains COMPOSITION a contains EVALUATION a_a[openEHR-EHR-EVALUATION.problem_diagnosis.v1]"
}

{
  "aql":  "select a/uid/value as uid, a_a/data[at0001]/items[at0002]/value/value as Causative_agent from EHR e[ehr_id/value='{{ehrId}}'] contains COMPOSITION a contains EVALUATION a_a[openEHR-EHR-EVALUATION.adverse_reaction_risk.v1]"
}

{
  "aql": "select   a/uid/value as uid, a/composer/name as author,a/context/start_time/value as date_created,   a_a/data[at0001]/events[at0002]/data[at0003]/items[at0005]/value/value as test_name,   a_a/data[at0001]/events[at0002]/data[at0003]/items[at0057]/value/value as conclusion,   a_a/data[at0001]/events[at0002]/data[at0003]/items[at0073]/value/value as status,   a_a/data[at0001]/events[at0002]/data[at0003]/items[at0075]/value/value as sample_taken,   a_a/data[at0001]/events[at0002]/data[at0003]/items[openEHR-EHR-CLUSTER.laboratory_test_panel.v0] as test_panel  from EHR e[ehr_id/value='{{ehrId}}'] contains COMPOSITION a[openEHR-EHR-COMPOSITION.report-result.v1] contains OBSERVATION a_a[openEHR-EHR-OBSERVATION.laboratory_test.v0] where a/name/value='Laboratory test report' and a/uid/value = '{{compositionId}}'"
}

{
  "aql": "select    e/ehr_id/value as ehrId,    e/ehr_status/subject/external_ref/id/value as subjectId,    e/ehr_status/subject/external_ref/namespace as subjectNamespace,    a/context/start_time/value as compositionStartTime,    a/uid/value as compositionId,    a/composer/name as composerName,    a/composer/external_ref/id/value as composerId,    a/composer/external_ref/namespace as composerNamespace,    b_a/uid/value as entryId,    b_a/description[at0001]/items[at0002] as Procedure_name,    b_a/description[at0001]/items[at0048] as Outcome,    b_a/description[at0001]/items[at0006] as Complication,    b_a/description[at0001]/items[at0067] as Procedure_type,    b_a/description[at0001]/items[at0063] as Body_site,    b_a/description[at0001]/items[at0014] as Reason,    b_a/description[at0001]/items[at0005]/value/value as Comment,    b_a/other_participations as OtherParticipations,    b_a/time/value as Procedure_time,    b_a/ism_transition/current_state/defining_code/code_string as Status_code,    b_a/ism_transition/careflow_step/defining_code/code_string as Careflow_step_code,    b_a/description[at0001]/items[at0049]/value/value as Description from EHR e[ehr_id/value='{{ehrId}}']  contains COMPOSITION a[openEHR-EHR-COMPOSITION.health_summary.v1] contains ACTION b_a[openEHR-EHR-ACTION.procedure.v1] where a/name/value='Procedures list'"
}

{
  "aql": "select a/uid/value as uid, b_a/data[at0001]/items[at0002]/name/value as Type, b_a/data[at0001]/items[at0002]/value/value as Clinical_note from EHR e [ehr_id/value = '{{ehrId}}'] contains COMPOSITION a[openEHR-EHR-COMPOSITION.encounter.v1] contains EVALUATION b_a[openEHR-EHR-EVALUATION.clinical_synopsis.v1] where a/name/value='Clinical Notes' and a/uid/value = '{{compositionId}}'"
}

{
  "aql": "select     a/uid/value as uid,     a/composer/name as author,     a/context/start_time/value as date_created,     b_a/activities[at0001]/description[at0009]/items[at0121]/value/value as Test_name,     b_b/time/value as date_ordered,     b_b/description[at0001]/items[at0017]/value/value as Test_ordered from EHR e [ehr_id/value = '{{ehrId}}'] contains COMPOSITION a[openEHR-EHR-COMPOSITION.request.v1] contains (     INSTRUCTION b_a[openEHR-EHR-INSTRUCTION.request-lab_test.v1] and     ACTION b_b[openEHR-EHR-ACTION.laboratory_test.v0]) where a/name/value='Laboratory order'"
}

{
  "aql":  "select a/uid/value as uid, a_a/activities[at0001]/description[at0002]/items[at0070]/value/value as Medication_item from EHR e[ehr_id/value='{{ehrId}}'] contains COMPOSITION a contains INSTRUCTION a_a[openEHR-EHR-INSTRUCTION.medication_order.v1]"
}
